    data(){
        return {
            id: this.makeid(15),

            blue: "#007bff",
            red: "#dc3545",

            prevHeight: null,
            prevWidth: null,
            prevXpos: null,
            prevYpos: null,
            prevFistMode: null,

            alerts: ['Move your web cam to a new position!', 'Try using your other hand!', 'Use the other side of your hand for a while!', 'Change your sitting position :)'],
            alertInd: -1,
            alertAnim: false,
            alertVal: 0,

            numLeft: 5,

            startUp: false,

            fistCount: 0,
            handCount: 0,

            height: 130,
            width: 100,
            xpos: 200,
            ypos: 200, 
            canvasHeight: 0,
            canvasWidth: 0,
            canvasHeightFull: 0,
            scale: 1,
            mainScreen: true,
            showImage: false,
            deleteScreen: false,
            fistMode: false,

            holder: [null],

            fistModeHold: 1,

            canPress: true, 
            
            
            fistAnim: -1,
            
            timer: null,
            elapsedTime: 0,
            lastTime: '',
        }
    },
    methods: {
        init(){
            if ('mediaDevices' in navigator && 'getUserMedia' in navigator.mediaDevices){
                navigator.mediaDevices.getUserMedia({video: true}).then(stream =>{
                    const videoPlayer = document.querySelector("video");
                    videoPlayer.srcObject = stream
                    videoPlayer.play()        
                    new Promise(() => {
                        setTimeout(() => {
                            var feed = document.getElementById('vid');
                            this.$data.canvasHeight = feed.clientHeight
                            this.$data.canvasWidth = feed.clientWidth
                            var feed2 = document.getElementById('display');
                            this.$data.canvasHeightFull = feed2.clientHeight
                            var c = document.getElementById("overlai");
                            
                            c.height = feed.clientHeight
                            c.width = feed.clientWidth

                            
                            this.scale = Math.random()*0.4 + 0.8
                            this.height = this.scale*130;
                            this.width = this.scale*100
                            this.xpos = (this.canvasWidth - this.width-5)*Math.random()
                            this.ypos = (this.canvasHeight - this.height)*Math.random()

                            this.drawTheRect("overlai", this.xpos, this.ypos, this.height, this.width, this.fistMode)
                            window.addEventListener('keydown', (e) => {
                                if(e.keyCode == 32 && e.target == document.body) {
                                    e.preventDefault();
                                    this.startGame();
                                }
                            });
                        }, 1000)
                    });
                })
            }
        },
        switchFist(t){
            
            this.fistMode = t;
            
            new Promise(() => {
                setTimeout(() => {
                this.fistModeHold = !t ? 1 : 2
                this.playSwitchSound()
                new Promise(() => {
                setTimeout(() => {
                    this.playSwitchSound()
                }, 200)
                }, 400)
            })
            
        });
        },
        startGame() {
            if(!this.startUp) this.startUp = true
            else if(this.deleteScreen) {
                return
            } else {
                if(this.elapsedTime == 0) {
                    this.timer = setInterval(() => {
                        this.elapsedTime += 30;
                    }, 30);
                }
                this.buttonPressed();
                
            }
        },
        playAlertSound() {
            var audio = new Audio('https://notificationsounds.com/soundfiles/a8849b052492b5106526b2331e526138/file-sounds-1125-insight.mp3');
            audio.play();
        },
        playSwitchSound() {
            var audio = new Audio('https://www.freesoundslibrary.com/wp-content/uploads/2018/01/ding-sound-effect.mp3');
            audio.play();
            
        },
        playPositiveSound() {
            var audio = new Audio('http://soundbible.com/mp3/Air Plane Ding-SoundBible.com-496729130.mp3');
            audio.play();
        },
        playNegativeSound() {
            var audio = new Audio('http://soundbible.com/mp3/Computer%20Error%20Alert-SoundBible.com-783113881.mp3');
            audio.play();
        },
        resetDisplay() {
            this.mainScreen = false;
            this.showImage = false;
            this.deleteScreen = false;
        },
        completeDelete() {
            this.playNegativeSound();
            this.resetDisplay()
            this.mainScreen = true;
            if(this.fistMode) this.fistCount--
            else this.handCount--
            this.numLeft++

        },
        undoButton() {
            this.playPositiveSound();
            this.resetDisplay()
            this.showImage = true;
        },
        doAlert() {
            this.playAlertSound()
            this.alertInd = Math.floor(Math.random() * Math.floor(this.alerts.length))
            new Promise(() => {
                setTimeout(() => {
                    this.alertAnim = true
                    setTimeout(() => {
                        this.alertAnim = false
                        this.alertInd = -1
                    }, 2000)
                }, 2000)
            })
        },
        deletePressed() {
            if(this.showImage) {
                this.playNegativeSound();
                this.resetDisplay()
                this.deleteScreen = true;
            }
            
            
        },
        setcanPress(val){
            this.canPress = val
        },
        buttonPressed(){
            if (this.canPress == false){
                return
            }
            if(this.elapsedTime > 0) {
                this.lastTime = this.elapsedTime.toString()
                this.elapsedTime = 0
            }
            
            this.canPress = false;
            new Promise(() => {
                setTimeout(() => {
                    this.setcanPress(true)
                }, 300)
            });
            //Play the sound
            this.playPositiveSound();
            

            if(this.fistMode) this.fistCount++
            else this.handCount++
            this.numLeft = this.numLeft - 1
            
            if(this.showImage) {
                // Send to database
                this.pushImage()
                
                
                
            }
            
            // Animation handling
            if(this.showImage)  this.fistAnim = !this.prevFistMode ? 1 : 2
            
            
            var canvas = document.getElementById('canvas');
            var feed = document.getElementById('vid');
            canvas.width = feed.clientWidth
            canvas.height = feed.clientHeight
            // console.log($(".feed").width())
            const ctx = canvas.getContext("2d");
            ctx.imageSmoothingEnabled = true;
            ctx.imageSmoothingQuality = "high";
            ctx.drawImage(document.querySelector("video"), 0, 0, canvas.width,canvas.height);

            this.prevFistMode = this.fistMode
            this.prevXpos = this.xpos
            this.prevYpos = this.ypos
            this.prevHeight = this.height
            this.prevWidth = this.width

            if(this.showImage) {
                new Promise(() => {
                    setTimeout(() => {
                        this.fistAnim = -1
                        this.holder[0] = this.prevFistMode
                        this.holder[1] = this.prevXpos 
                        this.holder[2] = this.prevYpos 
                        this.holder[3] = this.prevHeight
                        this.holder[4] = this.prevWidth
                        this.drawTheRect("overlaiTwo", this.prevXpos, this.prevYpos, this.prevHeight, this.prevWidth, this.prevFistMode)

                    }, 500)
                });
            } else {
                this.drawTheRect("overlaiTwo", this.prevXpos, this.prevYpos, this.prevHeight, this.prevWidth, this.prevFistMode)
            }


            this.scale = Math.random()*0.4 + 0.8
            this.height = this.scale*130;
            this.width = this.scale*100
            this.xpos = (this.canvasWidth - this.width)*Math.random()
            this.ypos = (this.canvasHeight - this.height)*Math.random()

            this.alertVal++ 
            if(this.alertVal == 28) {
                this.doAlert()
                this.alertVal = 0
            }

            if (this.numLeft==0) {
                this.switchFist(!this.fistMode)
                firestore.collection("Users").doc(this.userID).set({
                    fistScore: this.fistCount+this.fistScore,
                    openScore: this.handCount+this.openScore,
                }, {merge: true})
                this.numLeft = 5
            }
            this.$emit('updateyboi', {fistCount: (this.fistCount+this.fistScore), handCount: this.handCount+this.openScore})
            

            this.drawTheRect("overlai", this.xpos, this.ypos, this.height, this.width, this.fistMode)


            this.resetDisplay()
            this.showImage = true;

           
            // context.drawImage(imageObj, sourceX, sourceY, sourceWidth, sourceHeight, destX, destY, destWidth, destHeight);

            

        },
        pushImage(){
            var stor = storage.ref().child("images/" + this.makeid(15))

            var push = {    
                id: this.id,
                name: this.userID,
                height: this.prevHeight,
                width: this.prevWidth,
                xpos: this.prevXpos,
                ypos: this.prevYpos,
                label: this.prevFistMode ? 1 : 0,
                time: this.lastTime
            }       

            var canvas = document.getElementById('canvas');
            canvas.toBlob((blob) => {
                stor.put(blob).then(snapshot =>{
                    snapshot.ref.getDownloadURL().then(function(downloadURL) {
                        // console.log('File available at', downloadURL);
                        push.image = downloadURL
                        firestore.collection("Images").doc().set(push)
                    });
                })
            })
        },
        drawTheRect(nam, x, y, h, w, b) {
            var c = document.getElementById(nam);
            var ct = c.getContext("2d");
            ct.clearRect(0, 0, c.width, c.height);
            ct.beginPath();
            ct.strokeStyle = !b ? this.blue : this.red;
            ct.lineWidth = 3;
            ct.rect(x,y,h,w);
            ct.stroke();
        },
        makeid(length) {
            var result           = '';
            var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
            var charactersLength = characters.length;
            for ( var i = 0; i < length; i++ ) {
                result += characters.charAt(Math.floor(Math.random() * charactersLength));
            }
            return result;
        },
    },
    beforeMount() {
        this.init();
        
    }, 
    updated(){

    },
    computed: {
        updateFist: function(){
            let out = []
            out.push((this.fistAnim==1) ? 'pulse' : '')
            out.push(!this.fistMode ? 'text-secondary' : 'text-black')
            
            
            return out

        },
        updateHand: function(){
            let out = []
            out.push((this.fistAnim==2) ? 'pulse' : '')
            out.push(this.fistMode ? 'text-secondary' : 'text-black')
            
            
            return out

        },
        canvasAnim() {
            if(this.fistAnim==2) {
                return 'zoomOutLeft'
            } 
            if (this.fistAnim==1) {
                return 'rotateOutUpLeft'
            } 
            return ''
        },
        updateTotal: function(){
            return ((this.fistCount+this.fistScore+this.handCount+this.openScore)%10==0) && (this.fistAnim!=-1)
            
        },
        formattedElapsedTime() {
            const date = new Date(null);
            date.setSeconds(this.elapsedTime / 1000);
            let str = this.elapsedTime.toString()
            while (str.length < 3) {
                str = '0' + str
            }
            return [str.substring(0,str.length-3),str.substring(str.length-3, str.length-1)]
        }
    },
    mounted() {
        
        window.addEventListener('keydown', (e) => {

            if(e.keyCode == 32 && e.target == document.body) {
                e.preventDefault();
            }
            else if(e.code == "Delete" && e.target == document.body) {
                e.preventDefault();
                if(!this.deleteScreen)   this.deletePressed()
                else    this.completeDelete()
            }
        });
        //this.fistCount = this.fistScore
        //this.handCount = this.openScore


    }
}
</script>

