<script>
import 'bootstrap/dist/css/bootstrap.min.css'
export default {
  name: 'highscore',
  components: {

  },
  data() {
    return {
        colorBootstrap: ['primary', 'success', 'danger', 'warning', 'info', 'dark'],
        active: false,
        selected: -1,
        hovered: -1,
        user: '',
        testData: this.usersCollection,

        usersSorted: [{score: -1}, {score: -1}, {score: -1}],
    }
  },
  props: [
      'userName','current', 'usersCollection', 'userID', 'userTotalScore'
  ],
  methods: {
        mouseOver: function(){
            this.active = !this.active;   
        },
        changeInd: function(v) {
            this.selected = v;
        },
        changeIndHover: function(v) {
            this.hovered = v;
        },
        printUser: function() {
            console.log(this.userName)
        },
        updateTopThree: function() {
            let arr = [{score: -1},{score: -1},{score: -1}]
            
            Object.keys(this.usersCollection).forEach((key) => {
                let tempObj = {key: key, score: this.usersCollection[key].fistScore + this.usersCollection[key].openScore}
                if(tempObj.score > arr[0].score) {
                    arr.unshift(tempObj)
                    arr.pop()
                } else if(tempObj.score > arr[1].score) {
                    let holdme = arr[1]
                    arr[1] = tempObj
                    arr[2] = holdme
                } else if(tempObj.score > arr[2].score){ 
                    arr[2] = tempObj
                }
                
            })
            
            new Promise(() => setTimeout(() => {
                this.updateTopThree()
            }, 5000));
            
            this.usersSorted = arr
        },
    },
    computed: {
        submitButton: function() {
            var out = "bg-"
            out += (this.current==this.selected) ? 'secondary' : 'primary'
            return out 
        },
    },
    mounted: function() {
        this.updateTopThree();
        
    },
    watch: {
        testData: {
            handler: function(val){
                console.log("hi ", val)
            },
            deep:true
        },
        userTotalScore: {
            handler: function(val){
                console.log("hiaa ", val)
            },
        },
        usersCollection: {
            handler: function(val){
                console.log("hieeee ", val)
            },
            deep:true
        }
    }
}
</script>



